func getDiskTypeColor(diskType string) string {
	switch diskType {
	case "ssd":
		return "primary"
	case "hdd", "":
		return "secondary"
	default:
		return "info"
	}
}

func formatDiskTypes(diskTypes []string) string {
	if len(diskTypes) == 0 {
		return "Unknown"
	}
	if len(diskTypes) == 1 {
		return diskTypes[0]
	}
	// For multiple disk types, join with comma
	result := ""
	for i, diskType := range diskTypes {
		if i > 0 {
			result += ", "
		}
		result += diskType
	}
	return result
}

