// Helper functions for template
func getBucketStatusColor(status string) string {
	switch status {
	case "active":
		return "success"
	case "error":
		return "danger"
	case "warning":
		return "warning"
	default:
		return "secondary"
	}
}

func countActiveBuckets(buckets []dash.S3Bucket) int {
	count := 0
	for _, bucket := range buckets {
		if bucket.Status == "active" {
			count++
		}
	}
	return count
}

